class Hello{

 def log;
 def context;
 def testRunner;

 def Hello(log, context, testRunner){
  this.log = log
  this.context = context
  this.testRunner = testRunner
 }

 def sayHello(String name){
  log.info("Hello "+ name)
 }

}

context.setProperty("Hello", new Hello(log,context,testRunner))

project = testRunner.testCase.testSuite.project ;
 tcase = project.testSuites["TestSuite1"].testCases["TestCase1"] ; 
 hello = tcase.getTestStepByName("Hello");

hello.run(testRunner, context)

context.Hello.sayHello("Bob")

testRunner.testCase.testSuite.project.getTestSuiteByName('TestSuite1').getTestCaseByName('TestCase2').setSetupScript('log.info "setup for testcase 2"')
